$(function() {
    $(window).resize(videoresize);
	$("#bt_fs").fadeIn(500, "linear");
    $("#scene_top").fadeIn(500,"linear");
    $(".ok").fadeIn(500, "linear");
    $("#bt_fs").click(function(a) {
        aidn.util.fullscreen()
    });
    $("#btt_start").on('click', study);
    $("#btt_about").on('click', about);
});
function about(){
    $("#scene_top").fadeOut(500,"linear");
    $(".ok").fadeOut(500, "linear");
    $("#about").fadeIn(500, "linear");
    $("#about_cover").fadeIn(500, "linear");
    $("#bt_close").on('click', function(){
        $("#about").fadeOut(500, "linear");
        $("#about_cover").fadeOut(500, "linear");
        $("#scene_top").fadeIn(500,"linear");
        $(".ok").fadeIn(500, "linear");
    });
    $("#about_cover").on('click', function(){
        $("#about").fadeOut(500, "linear");
        $("#about_cover").fadeOut(500, "linear");
        $("#scene_top").fadeIn(500,"linear");
        $(".ok").fadeIn(500, "linear");
    });
}
function study(){
    $('.aplayer-play').trigger('click');
    $("#scene_top").fadeOut(500,"linear");
    $(".ok").fadeOut(500, "linear");
    $("#scene_learning").fadeIn(500, "linear");
    videoresize();
    $("#bt_back").fadeIn(500, "linear");
    $("#bt_back").on('click', function(){
        $('.aplayer-pause').trigger('click');
        $("#scene_top").fadeIn(500,"linear");
        $(".ok").fadeIn(500, "linear");
        $("#scene_learning").fadeOut(500, "linear");
        $("#bt_back").fadeOut(500, "linear");
    });
}
